package com.example.starsapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.SearchView;
import androidx.core.app.ShareCompat;
import androidx.core.view.MenuItemCompat;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;
import android.widget.Toast;

import com.example.starsapp.adapter.StarAdapter;
import com.example.starsapp.beans.Star;
import com.example.starsapp.service.StarService;

import java.util.ArrayList;
import java.util.List;

public class ListActivity extends AppCompatActivity {
    private StarService sts;
    private List<Star> stars;
    private RecyclerView recycle;
    private StarAdapter starAdapter = null;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list);
        recycle = findViewById(R.id.recycle_view);
        stars = new ArrayList<>();
        sts = StarService.getInstance();

        sts.create(new Star(1, "Rihanna", "https://resize-parismatch.lanmedia.fr/rcrop/250,250/img/var/news/storage/images/paris-match/people-a-z/rihanna/5971706-8-fre-FR/Rihanna.jpg", 3.5f));
        sts.create(new Star(2, "Johny deep", "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3b/Johnny_Depp-2757_%28cropped%29.jpg/220px-Johnny_Depp-2757_%28cropped%29.jpg", 2f));
        sts.create(new Star(3, "JLO", "https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTgwMTgyMjU4NjIyMTQ1ODgw/gettyimages-469873772.jpg", 1f));
        sts.create(new Star(4, "georges clooney", "https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cfl_progressive%2Cq_auto:good%2Cw_1200/MTgwNDYwMDg1ODI5NDQ1NDg0/gettyimages-969403912.jpg", 4.5f));
        sts.create(new Star(5, "leonardo dicaprio", "https://m.media-amazon.com/images/M/MV5BMjI0MTg3MzI0M15BMl5BanBnXkFtZTcwMzQyODU2Mw@@._V1_UY1200_CR130,0,630,1200_AL_.jpg", 3f));
        sts.create(new Star(6, "jennifer aniston", "data:image/jpeg;base64,", 2f));
        sts.create(new Star(7,"maluma", "https://i.scdn.co/image/ab6761610000e5ebc1be661291a4227bfc4e8669", 4f));
        sts.create(new Star(8, "shakira", "data:image/jpeg;base64,", 4.5f));
        sts.create(new Star(9,"messi", "https://imageio.forbes.com/specials-images/imageserve/5ec595d45f39760007b05c07/0x0.jpg?format=jpg&crop=1491,1490,x989,y74,safe&fit=crop", 2.5f));
        starAdapter = new StarAdapter(this, sts.findAll());
        recycle.setAdapter(starAdapter);
        recycle.setLayoutManager(new LinearLayoutManager(this));
    }
    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        if(item.getItemId() == R.id.share){
        String txt = "Stars";
        String mimeType = "text/plain";
        ShareCompat.IntentBuilder
                .from(this) .setType(mimeType)
                .setChooserTitle("Stars")
                .setText(txt)
                .startChooser();
    }
        return super.onOptionsItemSelected(item); }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu_main,menu);
        MenuItem menuItem = menu.findItem(R.id.app_bar_search);
        SearchView searchView = (SearchView) MenuItemCompat.getActionView(menuItem);
        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                return true;
            }
            @Override
            public boolean onQueryTextChange(String newText) {
                if (starAdapter != null){
                    starAdapter.getFilter().filter(newText);
                }
                return true;
            }
        });
        return true; }
}